mapper.insert:
INSERT INTO MAIN_CATEGORY(
MAIN_CATEGORY_ID,
MAIN_CATEGORY_NAME
) VALUES (
MAIN_CATEGORY_SEQ.NEXTVAL,
#{mainCategoryName}
)

INSERT INTO SUB_CATEGORY(
SUB_CATEGORY_ID,
MAIN_CATEGORY_ID,
SUB_CATEGORY_NAME
) VALUES (
SUB_CATEGORY_SEQ.NEXTVAL,
#{mainCategoryId},
#{subCategoryName}
)

mapper.select:
SELECT * FROM PRODUCT_CATEGORY WHERE PRODUCT_ID IN
#{id}
ORDER BY PRODUCT_ID ASC

SELECT * FROM ( SELECT PRODUCT.*, ROW_NUMBER() OVER (ORDER BY PRODUCT_ID DESC) AS RN FROM PRODUCT
PRODUCT_STOCK > 0
AND PRODUCT_NAME LIKE '%' || #{keyword} || '%'
) WHERE RN BETWEEN #{startNum} AND #{endNum}

SELECT * FROM (
    SELECT PRODUCT.*, ROW_NUMBER() OVER (ORDER BY CREATEAT DESC) AS RN 
    FROM PRODUCT

        SELLER_ID = #{sellerId}

) 
WHERE RN BETWEEN #{startNum} AND #{endNum}

SELECT COUNT(*)
FROM PRODUCT
WHERE SELLER_ID = #{sellerId}

mapper.update:
UPDATE MAIN_CATEGORY
SET MAIN_CATEGORY_NAME =
#{mainCategoryName}
WHERE MAIN_CATEGORY_ID = #{mainCategoryId}
